import { server as serverConfig, origins } from '../config'
import { devStream } from './stream'

const logOriginPass = (origin, pattern) => log.trace(`PASS: origin [${origin}] matches pattern [${pattern}].`)
const logOriginFail = (origin) => log.trace(`FAIL: origin [${origin}] does not match any patterns.`)
const logOriginDNE = () => log.trace('DNE: origin header does not exist.')

const corsValidator = patterns => {
  return req => (typeof req.headers.origin === 'string'
                ? patterns.filter(x => x.test(req.headers.origin))
                          .map(devStream(x => logOriginPass(req.headers.origin, x))).length > 0
                : true
                )
}

export function getCors() {
  const isOriginOk = origins ? corsValidator(origins.map(x => new RegExp(x))) : true

  const failureStatus = { code: 403
                        , message: '403 Forbidden'
                        }

  const getError = req => req.headers.origin  ? ({ message: logOriginFail(req.headers.origin), code: 10 })
                                              : ({ message: logOriginDNE(), code: 11 })

  const getOptionsHeaders = req => ({ 'Access-Control-Allow-Origin': req.headers.origin
                                    , 'Access-Control-Max-Age': 604800 // Specifies how long the preflight response can be cached in seconds
                                    , 'Access-Control-Allow-Methods': 'GET, PUT, POST, DELETE'
                                    , 'Access-Control-Allow-Headers': 'Content-Type, Authorization'
                                    , 'Access-Control-Allow-Credentials': true
                                    })

  const handle = (req, res) => {
    const resolvedOrigin = req.headers.origin || req.headers.host
    if(!isOriginOk(req))
      return req.headers.origin ? log.error(`proxy -origin [${resolvedOrigin}], method [${req.method}]`) : log.warn('proxy -host [%s], method [%s]', req.headers.host, req.method)
    log.trace(`proxy +${req.headers.origin ? 'origin' : 'host'} [${resolvedOrigin}], method [${req.method}]`)
    res.setHeader('Access-Control-Allow-Origin', resolvedOrigin)
    res.setHeader('Access-Control-Allow-Credentials', true)
  }

  const handlePreflight = (req, res) => {
    res.writeHead(200, getOptionsHeaders(req))
    res.end()
  }

  const handleFailure = (req, res) => {
    res.writeHead(failureStatus.code)
    res.end (JSON.stringify({ message: failureStatus.message
                            , errors: [ getError(req) ]
                            }))
  }

  return  { isOriginOk : isOriginOk
          , failureStatus: failureStatus
          , getError: getError
          , getOptionsHeaders: getOptionsHeaders
          , handle: handle
          , handlePreflight: handlePreflight
          , handleFailure: handleFailure
          }
}

/***
 * Proxy Module
 * Responsible for reading proxy config and setting up proxies.
 * @module server/proxy
 */
import httpProxy from 'http-proxy'
import http from 'http'
import https from 'https'
import { server as serverConfig, log } from '../config'

const proxyListen = ({ name, fromPort, toHost, toPort }) => `proxy ${name} directing from :${fromPort} to ${toHost}:${toPort}`

export default function proxy() {
  const cdnBindings = new Map(serverConfig.bindings.cdn)
  const hasHttp = cdnBindings.has('http')
  const hasHttps = cdnBindings.has('https')
  //const cors = getCors()
  const proxyConfigs = Array.isArray(serverConfig.proxies) ? serverConfig.proxies : [ serverConfig.proxies ]
  if(proxyConfigs.length === 0)
    return

  /*** Iterate proxy configs and start routing */
  proxyConfigs.forEach(config => {
    const options = getProxyOptions(config)
    const proxyServer = httpProxy.createProxyServer(options)
    const listenMessage = proxyListen(config)

    proxyServer.on('error', (err, req, res) => {
      log.error(err, 'error proxying request')
      res.end()
    })

    proxyServer.on('proxyReq', (proxyReq, req, res, options) => { /* This event fires on proxy requests */ })


    createProxyServer(http.createServer(onProxy))

    const createProxyServer = server => {
      if (config.allowWebSockets)
        server.on('upgrade', (req, socket, head) => server.ws(req, socket, head))
      server.listen(config.fromPort, () => log.info(listenMessage))
    }

    const onProxy = (req, res) => {
      //if(!cors.isOk(req))
        //return cors.handleFailure(req, res)

      if(config.stub && config.stub[req.method])
        return proxyStub(req, res)

      if(req.method === 'OPTIONS')
        //return cors.handlePreflight(req, res)

      if (config.latency)
        setTimeout(() => proxyServer.web(req, res), config.latency)
      else
        proxyServer.web(req, res)
    }

    const proxyStub = (req, res) => {
      var stub = config.stub[req.method]
      res.writeHead(200, stub.headers)
      if (stub.headers['content-length'] === 0) res.end()
      else res.end(JSON.stringify(stub.body))
    }
  })
}

const getProxyOptions = config => {
  var opts =  { target: config.target || `${config.toScheme}://${config.toHost}:config.toPort`
              , xfwd: true
              }
  opts.secure = config.secure
  return opts
}

import Immutable from 'immutable'

export const HYDRATE = '@@redux/INIT'

/** Tests if object is an immutable structure and if not returns a new state that is immutable. */
export default function hydrateImmutable(state, action = {}) {
  return [action.type === HYDRATE && !Immutable.Iterable.isIterable(state) ? Immutable.fromJS(state) : state, action]
}

import Immutable from 'immutable'
import hydrateImmutable, { HYDRATE } from '../hydrateImmutable'
import  { CLEAR_DATA
        , FETCH_DATA
        , RECEIVE_DATA
        , KEYED_DATA
        , AUTHORIZE_MIDDLEWARE
        , RECEIVE_AUTHORIZE_IDENTITY
        , RECEIVE_REFRESH_IDENTITY
        , RECEIVE_IMPERSONATE_IDENTITY
        } from '../constants'

function entities(state = Immutable.Map(), action = {}) {
  const { type, payload, error } = action
  if(error || !payload)
    return state
  const { apiName, actionName, actionKey, data } = payload
  switch(type) {
    case KEYED_DATA:
      return state.setIn([apiName, actionKey], data)
    case RECEIVE_DATA:
      return state.setIn([apiName, actionName], data)
    case CLEAR_DATA:
      return state.deleteIn([apiName, actionName])
  }
  return state
}

function isFetching(state = Immutable.Map(), action = {}) {
  const { type, payload, error } = action
  if(!type || !payload)
    return state
  const { apiName, actionName, actionKey, inputData, data } = payload
  switch(type) {
    case FETCH_DATA:
      return state.setIn([apiName, actionName], !error)
    case RECEIVE_DATA:
    case CLEAR_DATA:
      return state.setIn([apiName, actionName], false)
    case KEYED_DATA:
      return state.setIn([apiName, actionKey], false)
  }
  return state
}

export const initialState = { entities: entities()
                            , isFetching: isFetching()
                            }

export function api(state = initialState, action) {
  const { type, payload, error } = action
  switch(type) {
    case FETCH_DATA:
      return Object.assign({}, state, { isFetching: isFetching(state.isFetching, action) })
    case HYDRATE:
    case RECEIVE_DATA:
    case KEYED_DATA:
    case CLEAR_DATA:
      return Object.assign({}, state, { entities: entities(...hydrateImmutable(state.entities, action))
                                      , isFetching: isFetching(...hydrateImmutable(state.isFetching, action))
                                      })
  }
  return state
}

import  { UPDATE_INPUT_VALUE
        , CLEAR_SUGGESTIONS
        , FETCH_SUGGESTIONS
        , FILTER_SUGGESTIONS
        } from '../constants'


const digitsRE = /\(?\d+\)?/g
const wordsRE = /[\w\(\)]+/ig

const idFilter = (id, value) => {
  let inputDigits = value.match(digitsRE)
  if(!inputDigits)
    return false
  return inputDigits.some(x => `(${id})`.includes(x))
}

const nameFilter = (name, value) => {
  let inputWords = value.match(wordsRE)
  if(!inputWords)
    return false
  return inputWords.some(x => name.toLowerCase().includes(x.toLowerCase()))
}

const monikerFilter = (moniker, value) => {
  if(!value)
    return false
  return nameFilter(moniker.name, value) || idFilter(moniker.id, value)
}

function value(state = '', action = {}) {
  const { type, payload, error } = action
  switch (type) {
    case UPDATE_INPUT_VALUE:
      const { value } = payload
      return value
  }
  return state
}

function suggestions(state = [], action = {}) {
  const { type, payload, error } = action
  switch (type) {
    case CLEAR_SUGGESTIONS:
      return []
    case FILTER_SUGGESTIONS:
      if(state.value === payload.value)
        return payload.suggestions
      return payload.suggestions.filter(moniker => monikerFilter(moniker, payload.value)).sort((a, b) => (a.id - b.id)).slice(0, 12)
  }
  return state
}

function isLoading(state = false, action = {}) {
  const { type, payload, error } = action
  switch (type) {
    case FETCH_SUGGESTIONS:
      return true
    case FILTER_SUGGESTIONS:
      return false
  }
  return state
}

const initialState =  { value: value()
                      , suggestions: suggestions()
                      , isLoading: isLoading()
                      }

export function autocomplete(state = initialState, action) {
  const { type, payload, error } = action
  switch (type) {
    case UPDATE_INPUT_VALUE:
    case CLEAR_SUGGESTIONS:
    case FETCH_SUGGESTIONS:
    case FILTER_SUGGESTIONS:
      return Object.assign({}, state, { value: value(state.value, action)
                                      , suggestions: suggestions(state.suggestions, action)
                                      , isLoading: isLoading(state.isLoading, action)
                                      })
  }
  return state
}

import Immutable from 'immutable'
import  { FORGET_TOKENS
        , FORGET_FINGERPRINT
        , FETCH_IDENTITY
        , SET_IDENTITY
        , POST_AUTHORIZE
        , POST_AUTHORIZE_ADMIN
        , IDENTITY_INVALID
        , IDENTITY_EXPIRED
        //, DISMISS_ERROR
        , AUTHORIZE_MIDDLEWARE
        } from '../constants'

const getTixClaimSchema = name => `http://schemas.tix.com/identity/claims/${name}`
const getClaim = (decodedToken, name) => decodedToken[name]
const hasTixClaim = (decodedToken, name, value) => hasClaim(decodedToken, getTixClaimSchema(name), value)
const getTixClaim = (decodedToken, name) => getClaim(decodedToken, getTixClaimSchema(name))
const hasRole = (decodedToken, roleName) => hasClaim(decodedToken, getTixClaimSchema('role'), roleName)
const hasClaim = (decodedToken, name, value) => {
  let claim = getClaim(decodedToken, name)
  // if claim exists but value was not specified, return true
  if (claim && !value)
    return true
  if (claim)
    return Array.isArray(claim) ? claim.indexOf(value) !== -1 : claim === value
}


function tokens(state = null, action = {}) {
  const { type, payload, error } = action
  switch(type) {
    case SET_IDENTITY:
      if(error || !payload.tokens)
        return null
      return payload.tokens
    case FORGET_TOKENS:
    case IDENTITY_INVALID:
      return null
    case AUTHORIZE_MIDDLEWARE:
      return error ? null : state
  }
  return state
}

function fingerprint(state = null, action = {}) {
  const { type, payload, error } = action
  switch(type) {
    case FORGET_FINGERPRINT:
      return null
    case SET_IDENTITY:
      if(error)
        return state
      const { decodedToken } = payload
      return getTixClaim(decodedToken, 'fingerprint')
  }
  return state
}


function isFetching(state = false, action = {}) {
  const { type, payload, error } = action
  switch(type) {
    case FETCH_IDENTITY:
      return true
    case SET_IDENTITY:
      return false
  }
  return state
}

function isAuthorized(state = false, action = {}) {
  const { type, payload, error } = action
  switch(type) {
    case FORGET_TOKENS:
    case FORGET_FINGERPRINT:
    case IDENTITY_INVALID:
    //case IDENTITY_EXPIRED:
      return false
    case AUTHORIZE_MIDDLEWARE:
      return error ? false : state
    case SET_IDENTITY:
      return error ? false : true
  }
  return state
}

function isAdmin(state = false, action = {}) {
  const { type, payload, error } = action
  switch(type) {
    case FORGET_TOKENS:
    case FORGET_FINGERPRINT:
    case IDENTITY_INVALID:
    //case IDENTITY_EXPIRED:
      return false
    case AUTHORIZE_MIDDLEWARE:
      return error ? false : state
    case SET_IDENTITY:
      if(error)
        return false
      const { decodedToken } = payload
      const id = getTixClaim(decodedToken, 'actualorganizationid')
      return id === 1
  }
  return state
}

function subject(state = null, action = {}) {
  const { type, payload, error } = action
  if(error)
    return state
  switch(type) {
    case SET_IDENTITY:
      const { decodedToken } = payload
      return getClaim(decodedToken, 'sub')
  }
  return state
}

function user(state = null, action = {}) {
  const { type, payload, error } = action

  switch(type) {
    case FORGET_TOKENS:
    case FORGET_FINGERPRINT:
    case IDENTITY_INVALID:
    //case IDENTITY_EXPIRED:
      return null
    case AUTHORIZE_MIDDLEWARE:
      return error ? null : state
    case SET_IDENTITY:
      if(error)
        return null
      const { decodedToken } = payload
      let fullName = getTixClaim(decodedToken, 'username') // userfullname
      /** Probably should switch this parsing around to return 'userfirstname' and 'userlastname' */
      let sanitizedFullName = fullName ? fullName.trim() : 'Unknown Name'
      let nameParts = sanitizedFullName.split(' ').map(x => x.trim()).filter(x => x && x.length > 0)
      let [ firstName, ...lastNames ] = nameParts
      return  { username: getClaim(decodedToken, 'sub')
              , id: getTixClaim(decodedToken, 'userid')
              , fullName
              , firstName
              , lastName: lastNames.join(' ')
              }
  }
  return state
}

function actualUser(state = null, action = {}) {
  const { type, payload, error } = action
  switch(type) {
    case FORGET_TOKENS:
    case FORGET_FINGERPRINT:
    case IDENTITY_INVALID:
    //case IDENTITY_EXPIRED:
      return null
    case AUTHORIZE_MIDDLEWARE:
      return error ? null : state
    case SET_IDENTITY:
      if(error)
        return null
      const { decodedToken } = payload
      return  { username: getTixClaim(decodedToken, 'actualusername') || 'No Actual Username' // 'implement?'
              , id: getTixClaim(decodedToken, 'actualuserid') || 'No Actual User Id'
              , fullName: getTixClaim(decodedToken, 'actualfullName') || 'No Full Name' // 'implement?'
              , firstName: getTixClaim(decodedToken, 'actualfirstname') // 'implement?'
              , lastName: getTixClaim(decodedToken, 'actuallastname') // 'implement?'
              }
  }
  return state
}

function organization(state = null, action = {}) {
  const { type, payload, error } = action
  switch(type) {
    case FORGET_TOKENS:
    case FORGET_FINGERPRINT:
    case IDENTITY_INVALID:
    //case IDENTITY_EXPIRED:
      return null
    case AUTHORIZE_MIDDLEWARE:
      return error ? null : state
    case SET_IDENTITY:
      if(error)
        return null
      const { decodedToken } = payload
      return  { id: getTixClaim(decodedToken, 'organizationid')
              , name: getTixClaim(decodedToken, 'organization') // organizationname
              }
  }
  return state
}

function actualOrganization(state = null, action = {}) {
  const { type, payload, error } = action
  switch(type) {
    case FORGET_TOKENS:
    case FORGET_FINGERPRINT:
    case IDENTITY_INVALID:
    //case IDENTITY_EXPIRED:
      return null
    case AUTHORIZE_MIDDLEWARE:
      return error ? null : state
    case SET_IDENTITY:
      if(error)
        return null
      const { decodedToken } = payload
      let id = getTixClaim(decodedToken, 'actualorganizationid')
      return  { id
              , name: id === 1 ? 'Tix' : getTixClaim(decodedToken, 'organization') // actualorganizationname
              }
  }
  return state
}

const getTimeoutMS = timeoutMinutes => timeoutMinutes * 60000
const defaultTimeoutMS = getTimeoutMS(120)
/** Sliding session timeout from any point in time */
function timeoutMS(state = defaultTimeoutMS, action = {}) {
  const { type, payload, error } = action
  switch(type) {
    case FORGET_TOKENS:
    case FORGET_FINGERPRINT:
    case IDENTITY_INVALID:
    //case IDENTITY_EXPIRED:
      return defaultTimeoutMS
    case AUTHORIZE_MIDDLEWARE:
      return error ? defaultTimeoutMS : state
    case SET_IDENTITY:
      if(error)
        return defaultTimeoutMS
      const { decodedToken } = payload
      return getTimeoutMS(getTixClaim(decodedToken, 'timeout'))
  }
  return state
}

const inactiveWindowMS = 30000
const getInactiveMS = timeoutMinutes => timeoutMinutes * 60000 - inactiveWindowMS
const defaultInactiveMS = getInactiveMS(120)
/** Sliding session inactive from any point in time */
function inactiveMS(state = defaultInactiveMS, action = {}) {
  const { type, payload, error } = action
  switch(type) {
    case FORGET_TOKENS:
    case FORGET_FINGERPRINT:
    case IDENTITY_INVALID:
    //case IDENTITY_EXPIRED:
      return defaultInactiveMS
    case AUTHORIZE_MIDDLEWARE:
      return error ? defaultInactiveMS : state
    case SET_IDENTITY:
      if(error)
        return defaultInactiveMS
      const { decodedToken } = payload
      return getInactiveMS(getTixClaim(decodedToken, 'timeout'))
  }
  return state
}

/** What time (epoch) does token expire at */
function expiresAtMS(state = null, action = {}) {
  const { type, payload, error } = action
  switch(type) {
    case FORGET_TOKENS:
    case FORGET_FINGERPRINT:
    case IDENTITY_INVALID:
    //case IDENTITY_EXPIRED:
      return null
    case AUTHORIZE_MIDDLEWARE:
      return error ? null : state
    case SET_IDENTITY:
      if(error)
        return null
      const { decodedToken } = payload
      return decodedToken.exp * 1000
  }
  return state
}

/** How long of a window prior to expiration should refresh occur */
const refreshWindowMS = 40000
/** What time (epoch) should refresh occur at */
function refreshAtMS(state = null, action = {}) {
  const { type, payload, error } = action
  switch(type) {
    case FORGET_TOKENS:
    case FORGET_FINGERPRINT:
    case IDENTITY_INVALID:
    //case IDENTITY_EXPIRED:
      return null
    case AUTHORIZE_MIDDLEWARE:
      return error ? null : state
    case SET_IDENTITY:
      if(error)
        return null
      const { decodedToken } = payload
      return decodedToken.exp * 1000 - refreshWindowMS
  }
  return state
}

const initialState =  { isFetching: isFetching()
                      , isAuthorized: isAuthorized()
                      , isAdmin: isAdmin()
                      , subject: subject()
                      , user: user()
                      , actualUser: actualUser()
                      , organization: organization()
                      , actualOrganization: actualOrganization()
                      , timeoutMS: timeoutMS()
                      , inactiveMS: inactiveMS()
                      , expiresAtMS: expiresAtMS()
                      , refreshAtMS: refreshAtMS()
                      , tokens: tokens()
                      , fingerprint: fingerprint()
                      //, errors: errors()
                      }


export function identity(state = initialState, action) {
  const { type, payload, error } = action
  switch(type) {
    case FETCH_IDENTITY:
    case SET_IDENTITY:
    case FORGET_TOKENS:
    case FORGET_FINGERPRINT:
    case IDENTITY_INVALID:
    case IDENTITY_EXPIRED:
    case AUTHORIZE_MIDDLEWARE:
      return Object.assign({}, state, { isFetching: isFetching(state.isFetching, action)
                                      , isAuthorized: isAuthorized(state.isAuthorized, action)
                                      , isAdmin: isAdmin(state.isAdmin, action)
                                      , subject: subject(state.subject, action)
                                      , user: user(state.user, action)
                                      , actualUser: actualUser(state.actualUser, action)
                                      , organization: organization(state.organization, action)
                                      , actualOrganization: actualOrganization(state.actualOrganization, action)
                                      , timeoutMS: timeoutMS(state.timeoutMS, action)
                                      , inactiveMS: inactiveMS(state.inactiveMS, action)
                                      , expiresAtMS: expiresAtMS(state.expiresAtMS, action)
                                      , refreshAtMS: refreshAtMS(state.refreshAtMS, action)
                                      , tokens: tokens(state.tokens, action)
                                      , fingerprint: fingerprint(state.fingerprint, action)
                                      //, errors: errors(state.errors, action)
                                      })
  }
  return state
}

import Immutable from 'immutable'
import hydrateImmutable, { HYDRATE } from '../hydrateImmutable'
import  { SET_VALIDATED
        , CLEAR_VALIDATED
        } from '../constants'

const initialValidated =  [ ['recaptcha', false ]
                          ]
function validated(state = Immutable.Map(initialValidated), action = {}) {
  const { type, payload, error } = action
  if(error)
    return state
  switch(type) {
    case SET_VALIDATED:
      return state.set(payload.key, true)
    case CLEAR_VALIDATED:
      return state.set(payload.key, false)
  }
  return state
}

const initialState = { validated: validated() }

export function validation(state = initialState, action) {
  const { type, payload, error } = action
  switch(type) {
    case HYDRATE:
    case SET_VALIDATED:
    case CLEAR_VALIDATED:
      return Object.assign({}, state, { validated: validated(...hydrateImmutable(state.validated, action)) })
  }
  return state
}

import Immutable from 'immutable'
import { defaultTheme } from '../../../config'
import hydrateImmutable, { HYDRATE } from '../hydrateImmutable'
import  { TOGGLE_VISIBILITY
        , SET_VISIBILITY
        , SET_THEME
        , TOGGLE_EXPANDER
        , SET_EXPANDER
        , REGISTER_TOOLTIP
        , DISPOSE_TOOLTIP
        , SET_TEXT
       } from '../constants'

const REDUX_FORM_CHANGE = 'redux-form/CHANGE'

const nextValue = current => {
  let value = current.get('value')
  let options = current.get('options')
  let nextIndex = options.indexOf(value) + 1
  return nextIndex >= options.size ? options.first() : options.get(nextIndex)
}

function visibility(state = Immutable.Map(), action = {}) {
  const { type, payload, error } = action
  if(error || !payload)
    return state
  const { componentID, value, options } = payload
  switch(type) {
    case TOGGLE_VISIBILITY:
      const current = state.get(componentID)
      if(current) {
        const next = nextValue(current)
        return state.setIn([componentID, 'value'], next)
      }
      return state.set(componentID, Immutable.fromJS({ options, value }))
    case SET_VISIBILITY:
      if(state.has(componentID))
        return state.setIn([componentID, 'value'], value)
      return state.set(componentID, Immutable.fromJS({ options, value }))
  }
  return state
}

function expanders(state = Immutable.Map(), action = {}) {
  const { type, payload, error } = action
  if(error || !payload)
    return state
  const { componentID, value, initialExpanders } = payload
  switch(type) {
    case SET_EXPANDER:
      return state.set(componentID, value)
    case TOGGLE_EXPANDER:
      return state.update(componentID, initialExpanders, x => !x )
  }
  return state
}

function tooltip(state = Immutable.Map(), action = {}) {
  const { type, payload, error } = action
  if(error || !payload)
    return state
  const { componentID, props } = payload
  switch(type) {
    case REGISTER_TOOLTIP:
      state.set(componentID, props)
    case DISPOSE_TOOLTIP:
      state.remove(componentID)
  }
  return state
}

function text(state = Immutable.Map(), action = {}) {
  const { type, payload, error } = action
  if(error)
    return state
  switch(type) {
    case SET_TEXT:
      return state.merge(payload)
    case REDUX_FORM_CHANGE:
      const { field, value, form } = action
      if(form !== 'page')
        return state
      return state.set(field, value)
  }
  return state
}

const initialTheme = defaultTheme
function theme(state = initialTheme, action = {}) {
  const { type, payload, error } = action
  if(error || !payload)
    return state
  switch(type) {
    case SET_THEME:
      return payload.name
  }
  return state
}

const initialState = { visibility: visibility(), theme: theme(), expanders: expanders(), tooltip: tooltip(), text: text() }

export function visual(state = initialState, action) {
  const { type, payload, error } = action
  switch(type) {
    case HYDRATE:
      return Object.assign({}, state, { visibility: visibility(...hydrateImmutable(state.visibility, action))
                                      , expanders: expanders(...hydrateImmutable(state.expanders, action))
                                      , tooltip: tooltip(...hydrateImmutable(state.tooltip, action))
                                      , text: text(...hydrateImmutable(state.text, action))
                                      })
    case TOGGLE_VISIBILITY:
    case SET_VISIBILITY:
      return Object.assign({}, state, { visibility: visibility(state.visibility, action) })
    case SET_THEME:
      return Object.assign({}, state, { theme: theme(state.theme, action)})
    case TOGGLE_EXPANDER:
    case SET_EXPANDER:
      return Object.assign({}, state, { expanders: expanders(state.expanders, action)})
